CREATE TABLE IF NOT EXISTS `tkwp_calender` (
  `calender_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `calender_name` varchar(200) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `calender_date` date NOT NULL,
  `calender_time` time NOT NULL,
  `calender_detail` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `user_id` int(11) NOT NULL,
  `status` tinyint(4) NOT NULL,
  PRIMARY KEY (`calender_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1130 ;

